import React, { useState, useEffect } from 'react';
import { createPortal } from 'react-dom';

// Main application component that manages state and "routing"
const App = () => {
  // State to hold user information and their authentication token
  const [user, setUser] = useState(null);
  const [token, setToken] = useState(null);
  // State for page navigation. 'login' is the default page.
  const [currentPage, setCurrentPage] = useState('login');
  // State for showing a modal message to the user
  const [message, setMessage] = useState({ visible: false, text: '', type: 'info' });

  // Load Bootstrap CSS and JS dynamically when component mounts
  useEffect(() => {
    // Load Bootstrap CSS
    const link = document.createElement('link');
    link.href = 'https://cdn.jsdelivr.net/npm/bootstrap@5.3.3/dist/css/bootstrap.min.css';
    link.rel = 'stylesheet';
    link.integrity = 'sha384-QWTKZyjpPEjISv5WaRU9OFeRpok6YctnYmDr5pNlyT2bRjXh0JMhjY6hW+ALEwIH';
    link.crossOrigin = 'anonymous';
    document.head.appendChild(link);

    // Load Bootstrap JS
    const script = document.createElement('script');
    script.src = 'https://cdn.jsdelivr.net/npm/bootstrap@5.3.3/dist/js/bootstrap.bundle.min.js';
    script.integrity = 'sha384-YvpcrYf0tY3lHB60NNkmXc5s9fDVZLESaAA55NDzOxhy9GkcIdslK1eN7N6jIeHz';
    script.crossOrigin = 'anonymous';
    script.async = true;
    document.head.appendChild(script);

    // Cleanup function to remove the added elements when component unmounts
    return () => {
      document.head.removeChild(link);
      document.head.removeChild(script);
    };
  }, []);

  // Function to show a message modal
  const showMessage = (text, type = 'info') => {
    setMessage({ visible: true, text, type });
    setTimeout(() => {
      setMessage(prev => ({ ...prev, visible: false }));
    }, 3000);
  };

  // Handle successful login, receiving both user data and the auth token
  const handleLogin = (userData, authToken) => {
    setUser(userData);
    setToken(authToken);
    // After login, navigate to the appropriate dashboard based on user type
    setCurrentPage(userData.userType === 'customer' ? 'customerDashboard' : 'driverDashboard');
    showMessage(`Welcome back, ${userData.name}!`, 'success');
  };

  // Handle logout
  const handleLogout = () => {
    setUser(null);
    setToken(null);
    setCurrentPage('login');
    showMessage('You have been logged out.', 'info');
  };

  // The main rendering logic using a switch statement for "routing"
  const renderPage = () => {
    // If user is logged in, show the requested page or default to their dashboard
    if (user) {
      switch (currentPage) {
        case 'customerDashboard':
          // Customer dashboard for booking and viewing bookings
          return <CustomerDashboard user={user} token={token} showMessage={showMessage} />;
        case 'driverDashboard':
          // Driver dashboard for managing assignments
          return <DriverDashboard user={user} token={token} showMessage={showMessage} />;
        case 'bookings':
          // A generic bookings page that could be used by both
          return <BookingList user={user} token={token} showMessage={showMessage} />;
        default:
          // Default to the correct dashboard if the user is logged in but page is invalid
          return user.userType === 'customer' ?
            <CustomerDashboard user={user} token={token} showMessage={showMessage} /> :
            <DriverDashboard user={user} token={token} showMessage={showMessage} />;
      }
    } else {
      // If no user is logged in, show the login or register page
      switch (currentPage) {
        case 'register':
          return <Register onRegisterSuccess={() => setCurrentPage('login')} showMessage={showMessage} />;
        case 'login':
        default:
          return <Login onLoginSuccess={handleLogin} showMessage={showMessage} />;
      }
    }
  };

  return (
    <>
      <div className="d-flex flex-column align-items-center justify-content-center min-vh-100 bg-light p-4">
        {/* Navigation for the app */}
        <nav className="container-fluid p-4 bg-white shadow-sm rounded-3 mb-4 d-flex justify-content-between align-items-center">
          <h1 className="h4 fw-bold text-primary mb-0">Driver Booking</h1>
          <div className="d-flex align-items-center">
            {user ? (
              // Dynamic navigation for logged-in users based on their userType
              <>
                {user.userType === 'customer' && (
                  <button
                    onClick={() => setCurrentPage('customerDashboard')}
                    className="btn btn-primary me-2 rounded-3 shadow-sm"
                  >
                    Book a Driver
                  </button>
                )}
                {user.userType === 'driver' && (
                  <button
                    onClick={() => setCurrentPage('driverDashboard')}
                    className="btn btn-primary me-2 rounded-3 shadow-sm"
                  >
                    My Assignments
                  </button>
                )}
                <button
                  onClick={() => setCurrentPage('bookings')}
                  className="btn btn-info me-2 rounded-3 shadow-sm"
                >
                  My Bookings
                </button>
                <button
                  onClick={handleLogout}
                  className="btn btn-danger rounded-3 shadow-sm"
                >
                  Logout
                </button>
              </>
            ) : (
              // Navigation for logged-out users
              <>
                <button
                  onClick={() => setCurrentPage('login')}
                  className="btn btn-secondary me-2 rounded-3 shadow-sm"
                >
                  Login
                </button>
                <button
                  onClick={() => setCurrentPage('register')}
                  className="btn btn-success rounded-3 shadow-sm"
                >
                  Register
                </button>
              </>
            )}
          </div>
        </nav>
        <div className="container p-4 bg-white shadow rounded-3">
          {renderPage()}
        </div>

        {/* Message Modal */}
        {message.visible && createPortal(
          <div className={`toast show position-fixed bottom-0 end-0 m-3 ${message.type === 'success' ? 'bg-success' : message.type === 'error' ? 'bg-danger' : 'bg-secondary'}`} role="alert" aria-live="assertive" aria-atomic="true">
            <div className="toast-body text-white">
              {message.text}
            </div>
          </div>,
          document.body
        )}
      </div>
    </>
  );
};

// Customer Dashboard Component
const CustomerDashboard = ({ user, token, showMessage }) => {
  const [drivers, setDrivers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Fetch a list of available drivers
    const fetchDrivers = async () => {
      try {
        setError(null);
        const response = await fetch('http://localhost:5000/api/drivers/all-drivers');

        // Handle 404 - endpoint doesn't exist or server issues
        if (response.status === 404) {
          console.log('Drivers endpoint not found (404)');
          setDrivers([]);
          setError('The drivers service is currently unavailable. Please try again later.');
          showMessage('Drivers service temporarily unavailable.', 'error');
          return;
        }

        // Handle other HTTP errors
        if (!response.ok) {
          throw new Error(`Server error: ${response.status} ${response.statusText}`);
        }

        const data = await response.json();
        console.log('Drivers API response:', data);

        // Check if the response is the "no drivers found" message
        if (data.msg && data.msg.includes('No drivers found')) {
          console.log('No drivers found in database');
          setDrivers([]); // Set empty array so the UI shows "No drivers available"
          setError(null); // Clear any previous errors
          showMessage('No drivers are currently available in your area.', 'info');
        } else if (Array.isArray(data)) {
          // If data is an array of drivers, use it
          console.log('Found drivers array:', data.length);
          setDrivers(data);
          setError(null);
        } else if (data.drivers && Array.isArray(data.drivers)) {
          // If data has a drivers property that's an array, use it
          console.log('Found drivers in data.drivers:', data.drivers.length);
          setDrivers(data.drivers);
          setError(null);
        } else {
          // Fallback: unexpected response format
          console.log('Unexpected response format:', data);
          setDrivers([]);
          setError('Unable to load drivers. Please try again.');
          showMessage('Unable to load drivers. Please try again.', 'error');
        }
      } catch (err) {
        console.error('Error fetching drivers:', err);
        setDrivers([]); // Set empty array on error

        // More specific error handling
        if (err.name === 'TypeError' && err.message.includes('fetch')) {
          setError('Cannot connect to the server. Please check if the backend is running.');
          showMessage('Cannot connect to server. Please try again later.', 'error');
        } else {
          setError('Failed to load drivers. Please try again.');
          showMessage('Failed to load drivers.', 'error');
        }
      } finally {
        setLoading(false);
      }
    };

    fetchDrivers();
  }, [showMessage]);

  const handleBookDriver = (driverId) => {
    // This is a placeholder for the booking logic
    showMessage(`Booking driver with ID: ${driverId} (functionality to be implemented)`, 'info');
  };

  const handleRetry = () => {
    setLoading(true);
    setError(null);
    // Re-trigger the useEffect by updating a dependency or call fetchDrivers directly
    window.location.reload(); // Simple retry - you could make this more elegant
  };

  if (loading) {
    return (
      <div className="d-flex justify-content-center align-items-center" style={{ height: '12rem' }}>
        <div className="text-center">
          <div className="spinner-border text-primary mb-3" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
          <p className="text-muted h5">Loading drivers...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="p-4">
      <h2 className="h3 fw-bold mb-4">Welcome, {user.name}!</h2>
      <p className="h5 text-muted mb-4">Find and book a driver for your next trip.</p>

      {/* Error State */}
      {error && (
        <div className="alert alert-warning text-center mb-4" role="alert">
          <h5 className="alert-heading">Service Unavailable</h5>
          <p className="mb-3">{error}</p>
          <button 
            onClick={handleRetry} 
            className="btn btn-outline-primary"
          >
            Try Again
          </button>
        </div>
      )}

      {/* Drivers List */}
      <div className="d-grid gap-3">
        {drivers.length > 0 ? (
          drivers.map(driver => (
            <div key={driver._id} className="card p-3 rounded-3 shadow-sm border">
              <div className="d-flex align-items-center justify-content-between">
                <div>
                  <h3 className="h5 fw-semibold text-primary">{driver.user.name}</h3>
                  <p className="text-muted mb-1">Vehicle: {driver.vehicle.make} {driver.vehicle.model}</p>
                  <p className="text-muted mb-1">Rating: {driver.ratings.average.toFixed(1)} / 5</p>
                  <p className="text-muted fw-bold mb-0">Hourly Rate: ${driver.pricing.hourlyRate}</p>
                </div>
                <button
                  onClick={() => handleBookDriver(driver._id)}
                  className="btn btn-success rounded-3 shadow-sm"
                >
                  Book Now
                </button>
              </div>
            </div>
          ))
        ) : (
          // Only show "No drivers available" if there's no error
          !error && (
            <div className="text-center p-5">
              <div className="mb-4">
                <svg width="64" height="64" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg" className="text-muted">
                  <path d="M3 18h18v-2H3v2zm.5-5L12 8l8.5 5H21V8c0-.55-.45-1-1-1H4c-.55 0-1 .45-1 1v5h.5z" fill="currentColor"/>
                </svg>
              </div>
              <h4 className="text-muted mb-3">No Drivers Available</h4>
              <p className="text-muted">There are currently no drivers available in your area. Please check back later.</p>
              <button 
                onClick={handleRetry} 
                className="btn btn-outline-primary mt-3"
              >
                Refresh
              </button>
            </div>
          )
        )}
      </div>
    </div>
  );
};

// Driver Dashboard Component
const DriverDashboard = ({ user, token, showMessage }) => {
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (token) {
      const fetchDriverAssignments = async () => {
        try {
          console.log('Fetching driver assignments for user:', user.name);

          const response = await fetch('http://localhost:5000/api/bookings/mybookings', {
            method: 'GET',
            headers: {
              'Content-Type': 'application/json',
              'Authorization': `Bearer ${token}`
            }
          });

          console.log('Response status:', response.status);

          if (!response.ok) {
            const errorData = await response.json();
            console.error('API Error:', errorData);

            // Handle specific error cases
            if (response.status === 404 && errorData.msg === 'Driver profile not found') {
              showMessage('No driver profile found. Please contact admin to set up your driver account.', 'error');
              setBookings([]);
              return;
            }

            throw new Error(errorData.msg || `HTTP ${response.status}: Failed to fetch bookings`);
          }

          const data = await response.json();
          console.log('Received bookings:', data);

          // Handle both array response and object with bookings array
          const bookingsArray = Array.isArray(data) ? data : (data.bookings || []);
          setBookings(bookingsArray);

          if (data.message) {
            showMessage(data.message, 'info');
          }

        } catch (err) {
          console.error('Error fetching driver assignments:', err);
          showMessage(`Failed to load assignments: ${err.message}`, 'error');
        } finally {
          setLoading(false);
        }
      };
      fetchDriverAssignments();
    } else {
      setLoading(false);
      setBookings([]);
    }
  }, [token, showMessage, user.id]);

  const handleUpdateBookingStatus = async (bookingId, status) => {
    try {
      const response = await fetch(`http://localhost:5000/api/bookings/status/${bookingId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({ status })
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.msg || 'Failed to update booking status');
      }

      const updatedBooking = await response.json();

      // Update the booking in state
      setBookings(prevBookings => 
        prevBookings.map(booking => 
          booking._id === bookingId ? updatedBooking : booking
        )
      );

      showMessage(`Booking ${status} successfully!`, 'success');

    } catch (err) {
      console.error('Error updating booking status:', err);
      showMessage(`Failed to update booking: ${err.message}`, 'error');
    }
  };

  if (loading) {
    return (
      <div className="d-flex justify-content-center align-items-center" style={{ height: '12rem' }}>
        <p className="text-muted h5">Loading assignments...</p>
      </div>
    );
  }

  return (
    <div className="p-4">
      <h2 className="h3 fw-bold mb-4">Welcome, {user.name}!</h2>
      <p className="h5 text-muted mb-4">Here are your assigned bookings.</p>
      <div className="d-grid gap-3">
        {bookings.length > 0 ? (
          bookings.map(booking => (
            <div key={booking._id} className="card p-3 rounded-3 shadow-sm border">
              <div className="d-flex align-items-center justify-content-between">
                <div>
                  <h3 className="h5 fw-semibold text-primary">Booking #{booking._id.substring(0, 8)}...</h3>
                  <p className="text-muted mb-1">Customer: {booking.customer?.user?.name || booking.customer?.name || 'Unknown'}</p>
                  <p className="text-muted mb-1">Status: <span className={`badge ${booking.status === 'accepted' ? 'bg-success' : booking.status === 'pending' ? 'bg-warning' : 'bg-secondary'}`}>{booking.status}</span></p>
                  <p className="text-muted mb-1">Type: {booking.bookingType}</p>
                  <p className="text-muted mb-0">Date: {new Date(booking.createdAt).toLocaleDateString()}</p>
                </div>
                {booking.status === 'pending' && (
                  <div className="d-flex">
                    <button
                      onClick={() => handleUpdateBookingStatus(booking._id, 'accepted')}
                      className="btn btn-success me-2 rounded-3 shadow-sm"
                    >
                      Accept
                    </button>
                    <button
                      onClick={() => handleUpdateBookingStatus(booking._id, 'cancelled')}
                      className="btn btn-danger rounded-3 shadow-sm"
                    >
                      Decline
                    </button>
                  </div>
                )}
              </div>
            </div>
          ))
        ) : (
          <p className="text-center text-muted h5">You have no new assignments.</p>
        )}
      </div>
    </div>
  );
};

// Booking List component that fetches user-specific bookings
const BookingList = ({ user, token, showMessage }) => {
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (token) {
      const fetchBookings = async () => {
        try {
          const response = await fetch('http://localhost:5000/api/bookings/mybookings', {
            method: 'GET',
            headers: {
              'Content-Type': 'application/json',
              'Authorization': `Bearer ${token}`
            }
          });

          if (!response.ok) {
            const errorData = await response.json();
            console.error('API Error:', errorData);

            if (response.status === 404 && errorData.msg === 'Driver profile not found') {
              showMessage('No driver profile found. Please contact admin to set up your driver account.', 'error');
              setBookings([]);
              return;
            }

            throw new Error(errorData.msg || 'Failed to fetch bookings');
          }

          const data = await response.json();
          const bookingsArray = Array.isArray(data) ? data : (data.bookings || []);
          setBookings(bookingsArray);

        } catch (err) {
          console.error('Error fetching bookings:', err);
          showMessage('Failed to load bookings.', 'error');
        } finally {
          setLoading(false);
        }
      };
      fetchBookings();
    } else {
      setLoading(false);
      setBookings([]);
    }
  }, [token, showMessage]);

  if (loading) {
    return (
      <div className="d-flex justify-content-center align-items-center" style={{ height: '12rem' }}>
        <p className="text-muted h5">Loading bookings...</p>
      </div>
    );
  }

  return (
    <div className="p-4">
      <h2 className="h3 fw-bold mb-4">My Bookings</h2>
      <div className="d-grid gap-3">
        {bookings.length > 0 ? (
          bookings.map(booking => (
            <div key={booking._id} className="card p-3 rounded-3 shadow-sm border">
              <h3 className="h5 fw-semibold text-primary">Booking #{booking._id.substring(0, 8)}...</h3>
              <p className="text-muted mb-1">Customer: {booking.customer?.user?.name || booking.customer?.name || 'Unknown'}</p>
              <p className="text-muted mb-1">Driver: {booking.driver?.user?.name || booking.driver?.name || 'Unknown'}</p>
              <p className="text-muted mb-1">Status: <span className={`badge ${booking.status === 'accepted' ? 'bg-success' : booking.status === 'pending' ? 'bg-warning' : 'bg-secondary'}`}>{booking.status}</span></p>
              <p className="text-muted mb-1">Type: {booking.bookingType}</p>
              <p className="text-muted mb-0">Date: {new Date(booking.createdAt).toLocaleDateString()}</p>
            </div>
          ))
        ) : (
          <p className="text-center text-muted h5">You have no bookings.</p>
        )}
      </div>
    </div>
  );
};

// Login form component
const Login = ({ onLoginSuccess, showMessage }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);

  const handleLogin = async (e) => {
    e.preventDefault();
    setError(null);

    try {
      const response = await fetch('http://localhost:5000/api/auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || errorData.msg || 'Login failed');
      }

      const responseData = await response.json();
      console.log('Login successful:', responseData.user.name);

      const { token, user } = responseData;

      if (!token) {
        throw new Error('No token received from server');
      }
      if (!user) {
        throw new Error('No user data received from server');
      }

      onLoginSuccess(user, token);
      showMessage('Login successful!', 'success');

    } catch (err) {
      console.error('Login error:', err.message);
      setError(err.message);
      showMessage('Login failed: ' + err.message, 'error');
    }
  };

  return (
    <div className="d-flex justify-content-center align-items-center h-100 p-4">
      <div className="bg-white p-4 rounded-3 shadow-sm w-100" style={{maxWidth: '24rem'}}>
        <h2 className="h4 fw-bold mb-4 text-center">Login</h2>
        <form onSubmit={handleLogin}>
          {error && <div className="alert alert-danger mb-4 text-center" role="alert">{error}</div>}
          <div className="mb-3">
            <label className="form-label fw-semibold">Email</label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="form-control rounded-3"
              required
            />
          </div>
          <div className="mb-4">
            <label className="form-label fw-semibold">Password</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="form-control rounded-3"
              required
            />
          </div>
          <button
            type="submit"
            className="btn btn-primary w-100 py-2 fw-semibold rounded-3 shadow-sm"
          >
            Login
          </button>
        </form>
      </div>
    </div>
  );
};

// Registration form component
const Register = ({ onRegisterSuccess, showMessage }) => {
  // State for all form fields, including nested fields for drivers
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    phone: '',
    userType: 'customer',
    // New nested objects for driver-specific fields
    vehicle: {
      make: '',
      model: '',
      licensePlate: '',
    },
    pricing: {
      hourlyRate: 0,
    },
    bio: '',
  });
  const [loading, setLoading] = useState(false);

  // General handler for top-level form fields
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  // Specific handler for nested fields like vehicle or pricing
  const handleNestedChange = (e, parent) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [parent]: {
        ...prev[parent],
        [name]: value,
      }
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      console.log('Submitting registration:', formData);

      // Create a payload based on user type
      const payload = {
        name: formData.name,
        email: formData.email,
        password: formData.password,
        phone: formData.phone,
        userType: formData.userType,
      };

      // Conditionally add driver-specific fields to the payload
      if (formData.userType === 'driver') {
        payload.vehicle = formData.vehicle;
        payload.pricing = formData.pricing;
        payload.bio = formData.bio;
      }

      const response = await fetch('http://localhost:5000/api/auth/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(payload),
      });

      console.log('Registration response status:', response.status);

      let data;
      try {
        const responseText = await response.text();
        console.log('Raw response:', responseText);

        // Try to parse as JSON
        data = responseText ? JSON.parse(responseText) : {};
      } catch (parseError) {
        console.error('Failed to parse response as JSON:', parseError);
        throw new Error('Server returned invalid response format');
      }

      if (response.ok) {
        showMessage('Registration successful! Please login.', 'success');
        onRegisterSuccess();
      } else {
        const errorMsg = data.errors?.[0]?.msg || data.msg || `Registration failed (${response.status})`;
        showMessage(errorMsg, 'error');
        console.error('Registration failed:', data);
      }
    } catch (err) {
      const errorMessage = err.message || 'Network error. Please try again later.';
      showMessage(errorMessage, 'error');
      console.error('Registration error:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container p-4 bg-light rounded-3 shadow">
      <h2 className="h3 fw-bold text-center mb-4">Register</h2>
      <form onSubmit={handleSubmit} className="d-grid gap-3">
        <div className="form-group">
          <label className="form-label fw-semibold">Name</label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            className="form-control rounded-3"
            required
          />
        </div>
        <div className="form-group">
          <label className="form-label fw-semibold">Email</label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            className="form-control rounded-3"
            required
          />
        </div>
        <div className="form-group">
          <label className="form-label fw-semibold">Password</label>
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            className="form-control rounded-3"
            required
          />
        </div>
        <div className="form-group">
          <label className="form-label fw-semibold">Phone</label>
          <input
            type="tel"
            name="phone"
            value={formData.phone}
            onChange={handleChange}
            className="form-control rounded-3"
            required
          />
        </div>
        <div className="form-group">
          <label className="form-label fw-semibold">User Type</label>
          <select
            name="userType"
            value={formData.userType}
            onChange={handleChange}
            className="form-select rounded-3"
          >
            <option value="customer">Customer</option>
            <option value="driver">Driver</option>
          </select>
        </div>
        {/* Conditional rendering for driver-specific fields */}
        {formData.userType === 'driver' && (
          <>
            <hr className="my-3"/>
            <h4 className="h5 fw-bold mb-3">Driver Details</h4>
            <div className="form-group">
              <label className="form-label fw-semibold">Vehicle Make</label>
              <input
                type="text"
                name="make"
                value={formData.vehicle.make}
                onChange={(e) => handleNestedChange(e, 'vehicle')}
                className="form-control rounded-3"
                required
              />
            </div>
            <div className="form-group">
              <label className="form-label fw-semibold">Vehicle Model</label>
              <input
                type="text"
                name="model"
                value={formData.vehicle.model}
                onChange={(e) => handleNestedChange(e, 'vehicle')}
                className="form-control rounded-3"
                required
              />
            </div>
            <div className="form-group">
              <label className="form-label fw-semibold">License Plate</label>
              <input
                type="text"
                name="licensePlate"
                value={formData.vehicle.licensePlate}
                onChange={(e) => handleNestedChange(e, 'vehicle')}
                className="form-control rounded-3"
                required
              />
            </div>
            <div className="form-group">
              <label className="form-label fw-semibold">Hourly Rate ($)</label>
              <input
                type="number"
                name="hourlyRate"
                value={formData.pricing.hourlyRate}
                onChange={(e) => handleNestedChange(e, 'pricing')}
                className="form-control rounded-3"
                required
              />
            </div>
            <div className="form-group">
              <label className="form-label fw-semibold">Short Bio</label>
              <textarea
                name="bio"
                value={formData.bio}
                onChange={handleChange}
                className="form-control rounded-3"
                rows="3"
                required
              ></textarea>
            </div>
          </>
        )}
        <button
          type="submit"
          disabled={loading}
          className="btn btn-success w-100 py-2 fw-semibold rounded-3 shadow-sm"
        >
          {loading ? 'Registering...' : 'Register'}
        </button>
      </form>
    </div>
  );
};

export default App;
