import React, { useState, useEffect } from 'react';
import { createPortal } from 'react-dom';

// The main application component that manages state and "routing"
const App = () => {
  // State to hold user information and their authentication token
  const [user, setUser] = useState(null);
  const [token, setToken] = useState(null);
  // State for page navigation. 'login' is the default page.
  const [currentPage, setCurrentPage] = useState('login');
  // State for showing a modal message to the user
  const [message, setMessage] = useState({ visible: false, text: '', type: 'info' });

  // Function to show a message modal
  const showMessage = (text, type = 'info') => {
    setMessage({ visible: true, text, type });
    setTimeout(() => {
      setMessage(prev => ({ ...prev, visible: false }));
    }, 3000);
  };

  // Handle successful login, receiving both user data and the auth token
  const handleLogin = (userData, authToken) => {
    setUser(userData);
    setToken(authToken);
    // After login, navigate to the appropriate dashboard based on user type
    setCurrentPage(userData.userType === 'customer' ? 'customerDashboard' : 'driverDashboard');
    showMessage(`Welcome back, ${userData.name}!`, 'success');
  };

  // Handle logout
  const handleLogout = () => {
    setUser(null);
    setToken(null);
    setCurrentPage('login');
    showMessage('You have been logged out.', 'info');
  };

  // The main rendering logic using a switch statement for "routing"
  const renderPage = () => {
    // If user is logged in, show the requested page or default to their dashboard
    if (user) {
      switch (currentPage) {
        case 'customerDashboard':
          // Customer dashboard for booking and viewing bookings
          return <CustomerDashboard user={user} token={token} showMessage={showMessage} />;
        case 'driverDashboard':
          // Driver dashboard for managing assignments
          return <DriverDashboard user={user} token={token} showMessage={showMessage} />;
        case 'bookings':
          // A generic bookings page that could be used by both
          return <BookingList user={user} token={token} showMessage={showMessage} />;
        default:
          // Default to the correct dashboard if the user is logged in but page is invalid
          return user.userType === 'customer' ?
            <CustomerDashboard user={user} token={token} showMessage={showMessage} /> :
            <DriverDashboard user={user} token={token} showMessage={showMessage} />;
      }
    } else {
      // If no user is logged in, show the login or register page
      switch (currentPage) {
        case 'register':
          return <Register onRegisterSuccess={() => setCurrentPage('login')} showMessage={showMessage} />;
        case 'login':
        default:
          return <Login onLoginSuccess={handleLogin} showMessage={showMessage} />;
      }
    }
  };

  return (
    <>
      {/* ADDED: Tailwind CSS CDN to make the styles work without a build process */}
      <script src="https://cdn.tailwindcss.com"></script>
      <div className="min-h-screen bg-gray-100 font-sans antialiased text-gray-800 p-4 flex flex-col items-center justify-center">
        {/* Navigation for the app */}
        <nav className="w-full max-w-4xl p-4 bg-white shadow-lg rounded-lg mb-4 flex justify-between items-center">
          <h1 className="text-2xl font-bold text-indigo-600">Driver Booking</h1>
          <div>
            {user ? (
              // Dynamic navigation for logged-in users based on their userType
              <>
                {user.userType === 'customer' && (
                  <button
                    onClick={() => setCurrentPage('customerDashboard')}
                    className="px-4 py-2 mr-2 bg-indigo-500 text-white rounded-md shadow-md hover:bg-indigo-600 transition-colors"
                  >
                    Book a Driver
                  </button>
                )}
                {user.userType === 'driver' && (
                  <button
                    onClick={() => setCurrentPage('driverDashboard')}
                    className="px-4 py-2 mr-2 bg-indigo-500 text-white rounded-md shadow-md hover:bg-indigo-600 transition-colors"
                  >
                    My Assignments
                  </button>
                )}
                <button
                  onClick={() => setCurrentPage('bookings')}
                  className="px-4 py-2 mr-2 bg-indigo-500 text-white rounded-md shadow-md hover:bg-indigo-600 transition-colors"
                >
                  My Bookings
                </button>
                <button
                  onClick={handleLogout}
                  className="px-4 py-2 bg-red-500 text-white rounded-md shadow-md hover:bg-red-600 transition-colors"
                >
                  Logout
                </button>
              </>
            ) : (
              // Navigation for logged-out users
              <>
                <button
                  onClick={() => setCurrentPage('login')}
                  className="px-4 py-2 mr-2 bg-gray-500 text-white rounded-md shadow-md hover:bg-gray-600 transition-colors"
                >
                  Login
                </button>
                <button
                  onClick={() => setCurrentPage('register')}
                  className="px-4 py-2 bg-teal-500 text-white rounded-md shadow-md hover:bg-teal-600 transition-colors"
                >
                  Register
                </button>
              </>
            )}
          </div>
        </nav>
        <div className="w-full max-w-4xl bg-white shadow-lg rounded-lg p-6">
          {renderPage()}
        </div>

        {/* Message Modal */}
        {message.visible && createPortal(
          <div className={`fixed top-4 right-4 p-4 rounded-lg shadow-xl text-white z-50 transition-all duration-300 transform ${message.visible ? 'translate-x-0' : 'translate-x-full'} ${message.type === 'success' ? 'bg-green-500' : message.type === 'error' ? 'bg-red-500' : 'bg-gray-700'}`}>
            {message.text}
          </div>,
          document.body
        )}
      </div>
    </>
  );
};

// Customer Dashboard Component
const CustomerDashboard = ({ user, token, showMessage }) => {
  const [drivers, setDrivers] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch a list of available drivers
    const fetchDrivers = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/drivers/');
        if (!response.ok) {
          throw new Error('Failed to fetch drivers');
        }
        const data = await response.json();
        setDrivers(data);
      } catch (err) {
        console.error('Error fetching drivers:', err);
        showMessage('Failed to load drivers.', 'error');
      } finally {
        setLoading(false);
      }
    };
    fetchDrivers();
  }, [showMessage]);

  const handleBookDriver = (driverId) => {
    // This is a placeholder for the booking logic
    showMessage(`Booking driver with ID: ${driverId} (functionality to be implemented)`, 'info');
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-48">
        <p className="text-gray-500 text-lg">Loading drivers...</p>
      </div>
    );
  }

  return (
    <div className="p-4">
      <h2 className="text-3xl font-bold mb-6 text-gray-900">Welcome, {user.name}!</h2>
      <p className="text-xl text-gray-600 mb-6">Find and book a driver for your next trip.</p>
      <div className="space-y-4">
        {drivers.length > 0 ? (
          drivers.map(driver => (
            <div key={driver._id} className="p-4 bg-gray-50 rounded-lg shadow-md border border-gray-200 flex items-center justify-between">
              <div>
                <h3 className="text-xl font-semibold text-indigo-700">{driver.user.name}</h3>
                <p className="text-gray-600">Vehicle: {driver.vehicle.make} {driver.vehicle.model}</p>
                <p className="text-gray-600">Rating: {driver.ratings.average.toFixed(1)} / 5</p>
                <p className="text-gray-600 font-bold">Hourly Rate: ${driver.pricing.hourlyRate}</p>
              </div>
              <button
                onClick={() => handleBookDriver(driver._id)}
                className="px-4 py-2 bg-green-500 text-white rounded-md shadow-md hover:bg-green-600 transition-colors"
              >
                Book Now
              </button>
            </div>
          ))
        ) : (
          <p className="text-center text-gray-500 text-lg">No drivers available at the moment.</p>
        )}
      </div>
    </div>
  );
};

// Driver Dashboard Component
const DriverDashboard = ({ user, token, showMessage }) => {
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch all bookings and filter for the current driver on the front end
    if (token) {
      const fetchDriverAssignments = async () => {
        try {
          // This endpoint is assumed to be working for all bookings
          const response = await fetch('http://localhost:5000/api/bookings/', {
            headers: {
              'x-auth-token': token
            }
          });
          if (!response.ok) {
            throw new Error('Failed to fetch bookings');
          }
          const allBookings = await response.json();
          // Filter bookings to show only those assigned to the current driver
          const driverBookings = allBookings.filter(booking => booking.driver.user._id === user._id);
          setBookings(driverBookings);
        } catch (err) {
          console.error('Error fetching driver assignments:', err);
          showMessage('Failed to load your assignments.', 'error');
        } finally {
          setLoading(false);
        }
      };
      fetchDriverAssignments();
    } else {
      setLoading(false);
      setBookings([]);
    }
  }, [token, showMessage, user._id]); // Re-run effect when user token or ID changes

  const handleUpdateBookingStatus = (bookingId, status) => {
    // Placeholder for API call to update booking status
    showMessage(`Updating booking ${bookingId} to status: ${status} (functionality to be implemented)`, 'info');
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-48">
        <p className="text-gray-500 text-lg">Loading assignments...</p>
      </div>
    );
  }

  return (
    <div className="p-4">
      <h2 className="text-3xl font-bold mb-6 text-gray-900">Welcome, {user.name}!</h2>
      <p className="text-xl text-gray-600 mb-6">Here are your assigned bookings.</p>
      <div className="space-y-4">
        {bookings.length > 0 ? (
          bookings.map(booking => (
            <div key={booking._id} className="p-4 bg-gray-50 rounded-lg shadow-md border border-gray-200 flex items-center justify-between">
              <div>
                <h3 className="text-xl font-semibold text-indigo-700">Booking #{booking._id.substring(0, 8)}...</h3>
                <p className="text-gray-600">Customer: {booking.customer.user.name}</p>
                <p className="text-gray-600">Status: {booking.status}</p>
                <p className="text-gray-600">Date: {new Date(booking.createdAt).toLocaleDateString()}</p>
              </div>
              <div className="flex space-x-2">
                <button
                  onClick={() => handleUpdateBookingStatus(booking._id, 'accepted')}
                  className="px-4 py-2 bg-green-500 text-white rounded-md shadow-md hover:bg-green-600 transition-colors"
                >
                  Accept
                </button>
                <button
                  onClick={() => handleUpdateBookingStatus(booking._id, 'declined')}
                  className="px-4 py-2 bg-red-500 text-white rounded-md shadow-md hover:bg-red-600 transition-colors"
                >
                  Decline
                </button>
              </div>
            </div>
          ))
        ) : (
          <p className="text-center text-gray-500 text-lg">You have no new assignments.</p>
        )}
      </div>
    </div>
  );
};

// Booking List component that fetches user-specific bookings
// Reusing this component to handle a generic "My Bookings" page for both user types
const BookingList = ({ user, token, showMessage }) => {
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (token) {
      const fetchBookings = async () => {
        try {
          const endpoint = user.userType === 'customer' ? 'api/bookings/mybookings' : 'api/drivers/mybookings';
          const response = await fetch(`http://localhost:5000/${endpoint}`, {
            headers: {
              'x-auth-token': token
            }
          });
          if (!response.ok) {
            throw new Error('Failed to fetch bookings');
          }
          const data = await response.json();
          setBookings(data);
        } catch (err) {
          console.error('Error fetching bookings:', err);
          showMessage('Failed to load bookings.', 'error');
        } finally {
          setLoading(false);
        }
      };
      fetchBookings();
    } else {
      setLoading(false);
      setBookings([]);
    }
  }, [token, showMessage, user.userType]);

  if (loading) {
    return (
      <div className="flex justify-center items-center h-48">
        <p className="text-gray-500 text-lg">Loading bookings...</p>
      </div>
    );
  }

  return (
    <div className="p-4">
      <h2 className="text-3xl font-bold mb-6 text-gray-900">My Bookings</h2>
      <div className="space-y-4">
        {bookings.length > 0 ? (
          bookings.map(booking => (
            <div key={booking._id} className="p-4 bg-gray-50 rounded-lg shadow-md border border-gray-200">
              <h3 className="text-xl font-semibold text-indigo-700">Booking #{booking._id.substring(0, 8)}...</h3>
              <p className="text-gray-600">Customer: {booking.customer.user.name}</p>
              <p className="text-gray-600">Driver: {booking.driver.user.name}</p>
              <p className="text-gray-600">Status: {booking.status}</p>
              <p className="text-gray-600">Date: {new Date(booking.createdAt).toLocaleDateString()}</p>
            </div>
          ))
        ) : (
          <p className="text-center text-gray-500 text-lg">You have no bookings.</p>
        )}
      </div>
    </div>
  );
};

