        </footer>
      </div>

      {/* Message Modal */}
      {message.visible && createPortal(
        <div className={`toast show position-fixed bottom-0 end-0 m-3 ${message.type === 'success' ? 'bg-success' : message.type === 'error' ? 'bg-danger' : 'bg-secondary'}`} role="alert" aria-live="assertive" aria-atomic="true">
          <div className="toast-body text-white">
            {message.text}
          </div>
        </div>,
        document.body
      )}
    </>
  );
};

// Admin Dashboard Component
const AdminDashboard = ({ user, token, showMessage }) => {
  const [activeTab, setActiveTab] = useState('dashboard');
  const [users, setUsers] = useState([]);
  const [bookings, setBookings] = useState([]);
  const [drivers, setDrivers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [stats, setStats] = useState({
    totalUsers: 0,
    totalBookings: 0,
    totalDrivers: 0,
    pendingBookings: 0,
    completedBookings: 0,
    revenue: 0
  });

  useEffect(() => {
    const fetchAdminData = async () => {
      try {
        // Fetch users
        const usersResponse = await fetch('http://localhost:5000/api/admin/users', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`
          }
        });
        if (!usersResponse.ok) throw new Error('Failed to fetch users');
        const usersData = await usersResponse.json();
        setUsers(usersData);

        // Fetch bookings
        const bookingsResponse = await fetch('http://localhost:5000/api/admin/bookings', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`
          }
        });
        if (!bookingsResponse.ok) throw new Error('Failed to fetch bookings');
        const bookingsData = await bookingsResponse.json();
        setBookings(bookingsData);

        // Fetch drivers
        const driversResponse = await fetch('http://localhost:5000/api/admin/drivers', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`
          }
        });
        if (!driversResponse.ok) throw new Error('Failed to fetch drivers');
        const driversData = await driversResponse.json();
        setDrivers(driversData);

        // Calculate stats
        const totalUsers = usersData.length;
        const totalBookings = bookingsData.length;
        const totalDrivers = driversData.length;
        const pendingBookings = bookingsData.filter(b => b.status === 'pending').length;
        const completedBookings = bookingsData.filter(b => b.status === 'completed').length;
        const revenue = bookingsData
          .filter(b => b.status === 'completed')
          .reduce((sum, booking) => sum + (booking.pricing?.totalAmount || 0), 0);

        setStats({
          totalUsers,
          totalBookings,
          totalDrivers,
          pendingBookings,
          completedBookings,
          revenue
        });
      } catch (err) {
        console.error('Error fetching admin data:', err);
        showMessage('Failed to load data.', 'error');
      } finally {
        setLoading(false);
      }
    };

    fetchAdminData();
  }, [token, showMessage]);

  const handleDeleteUser = async (userId) => {
    if (window.confirm('Are you sure you want to delete this user?')) {
      try {
        const response = await fetch(`http://localhost:5000/api/admin/users/${userId}`, {
          method: 'DELETE',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`
          }
        });
        if (!response.ok) throw new Error('Failed to delete user');
        setUsers(users.filter(user => user._id !== userId));
        showMessage('User deleted successfully.', 'success');
      } catch (err) {
        console.error('Error deleting user:', err);
        showMessage('Failed to delete user.', 'error');
      }
    }
  };

  const handleDeleteBooking = async (bookingId) => {
    if (window.confirm('Are you sure you want to delete this booking?')) {
      try {
        const response = await fetch(`http://localhost:5000/api/admin/bookings/${bookingId}`, {
          method: 'DELETE',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`
          }
        });
        if (!response.ok) throw new Error('Failed to delete booking');
        setBookings(bookings.filter(booking => booking._id !== bookingId));
        showMessage('Booking deleted successfully.', 'success');
      } catch (err) {
        console.error('Error deleting booking:', err);
        showMessage('Failed to delete booking.', 'error');
      }
    }
  };

  if (loading) {
    return (
      <div className="d-flex justify-content-center align-items-center" style={{ height: '12rem' }}>
        <div className="text-center">
          <div className="spinner-border text-primary mb-3" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
          <p className="text-muted h5">Loading admin dashboard...</p>
        </div>
      </div>
    );
  }

  return (
