                    >
                      <option value="cash">MomoPay Code 123456</option>
                      <option value="mtn_money">MTN Money</option>
                      <option value="airtel_money">Airtel Money</option>
                      <option value="card">Card</option>
                      <option value="paypal">PayPal</option>
