import React, { useState } from 'react';
import { createRoot } from 'react-dom/client';
import { CalendarDays, MapPin, CarFront, UsersRound, CalendarPlus, Clock } from 'lucide-react';

// Use a simple switch statement for "page" navigation
const App = () => {
  const [activePage, setActivePage] = useState('home');

  const renderPage = () => {
    switch (activePage) {
      case 'home':
        return <HomePage setActivePage={setActivePage} />;
      case 'booking':
        return <BookingPage setActivePage={setActivePage} />;
      default:
        return <HomePage setActivePage={setActivePage} />;
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 font-sans text-gray-800 flex flex-col items-center justify-center p-4 sm:p-8">
      {renderPage()}
    </div>
  );
};

const HomePage = ({ setActivePage }) => {
  return (
    <div className="bg-white rounded-3xl shadow-xl overflow-hidden max-w-lg w-full p-6 sm:p-8 text-center flex flex-col items-center">
      <h1 className="text-4xl sm:text-5xl font-extrabold text-blue-800 mb-4">Ride On Demand</h1>
      <p className="text-md sm:text-lg text-gray-600 mb-8">Your next journey is just a tap away.</p>

      <div className="w-24 h-24 sm:w-32 sm:h-32 rounded-full bg-blue-100 flex items-center justify-center mx-auto mb-8 animate-pulse">
        <CarFront size={80} className="text-blue-500" />
      </div>

      <button
        onClick={() => setActivePage('booking')}
        className="w-full sm:w-auto px-8 py-4 bg-blue-600 text-white font-bold text-xl rounded-full shadow-lg hover:bg-blue-700 transition-colors duration-300 transform hover:scale-105"
      >
        Book Now
      </button>

      <p className="mt-6 text-sm text-gray-500">Fast, reliable, and convenient.</p>
    </div>
  );
};

const BookingPage = ({ setActivePage }) => {
  const [pickup, setPickup] = useState('');
  const [dropoff, setDropoff] = useState('');
  const [date, setDate] = useState('');
  const [time, setTime] = useState('');
  const [passengers, setPassengers] = useState(1);
  const [isBooked, setIsBooked] = useState(false);
  const [isBooking, setIsBooking] = useState(false);

  const handleBooking = (e) => {
    e.preventDefault();
    setIsBooking(true);
    // Simulate API call or processing delay
    setTimeout(() => {
      setIsBooking(false);
      setIsBooked(true);
    }, 2000);
  };

  if (isBooked) {
    return (
      <div className="bg-white rounded-3xl shadow-xl max-w-lg w-full p-6 sm:p-8 text-center flex flex-col items-center">
        <h2 className="text-3xl sm:text-4xl font-bold text-green-600 mb-4">Booking Confirmed!</h2>
        <p className="text-lg text-gray-700 mb-6">Your driver is on the way to pick you up at:</p>
        <div className="bg-green-50 p-4 sm:p-6 rounded-2xl w-full text-left">
          <p className="text-md sm:text-lg font-semibold text-gray-800 mb-2">{pickup}</p>
          <p className="text-sm text-gray-500 flex items-center"><CalendarDays size={16} className="mr-2 text-green-500" /> {date}</p>
          <p className="text-sm text-gray-500 flex items-center mt-1"><Clock size={16} className="mr-2 text-green-500" /> {time}</p>
        </div>
        <button
          onClick={() => { setActivePage('home'); setIsBooked(false); }}
          className="mt-8 w-full sm:w-auto px-6 py-3 bg-blue-600 text-white font-semibold rounded-full shadow-lg hover:bg-blue-700 transition-colors duration-300"
        >
          Back to Home
        </button>
      </div>
    );
  }

  return (
    <div className="bg-white rounded-3xl shadow-xl max-w-lg w-full p-6 sm:p-8">
      <h2 className="text-3xl sm:text-4xl font-bold text-blue-800 mb-6 text-center">Book a Ride</h2>
      <form onSubmit={handleBooking} className="space-y-6">
        <div>
          <label htmlFor="pickup" className="block text-sm font-medium text-gray-700 mb-1 flex items-center">
            <MapPin size={18} className="mr-2 text-blue-500" /> Pickup Location
          </label>
          <input
            type="text"
            id="pickup"
            value={pickup}
            onChange={(e) => setPickup(e.target.value)}
            required
            placeholder="Enter pickup location"
            className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-colors duration-200"
          />
        </div>
        <div>
          <label htmlFor="dropoff" className="block text-sm font-medium text-gray-700 mb-1 flex items-center">
            <MapPin size={18} className="mr-2 text-blue-500" /> Drop-off Location
          </label>
          <input
            type="text"
            id="dropoff"
            value={dropoff}
            onChange={(e) => setDropoff(e.target.value)}
            required
            placeholder="Enter drop-off location"
            className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-colors duration-200"
          />
        </div>
        <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
          <div>
            <label htmlFor="date" className="block text-sm font-medium text-gray-700 mb-1 flex items-center">
              <CalendarDays size={18} className="mr-2 text-blue-500" /> Date
            </label>
            <input
              type="date"
              id="date"
              value={date}
              onChange={(e) => setDate(e.target.value)}
              required
              className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-colors duration-200"
            />
          </div>
          <div>
            <label htmlFor="time" className="block text-sm font-medium text-gray-700 mb-1 flex items-center">
              <Clock size={18} className="mr-2 text-blue-500" /> Time
            </label>
            <input
              type="time"
              id="time"
              value={time}
              onChange={(e) => setTime(e.target.value)}
              required
              className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-colors duration-200"
            />
          </div>
        </div>
        <div>
          <label htmlFor="passengers" className="block text-sm font-medium text-gray-700 mb-1 flex items-center">
            <UsersRound size={18} className="mr-2 text-blue-500" /> Passengers
          </label>
          <input
            type="number"
            id="passengers"
            value={passengers}
            onChange={(e) => setPassengers(e.target.value)}
            min="1"
            required
            className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-colors duration-200"
          />
        </div>

        <button
          type="submit"
          disabled={isBooking}
          className="w-full px-6 py-4 bg-blue-600 text-white font-bold text-xl rounded-full shadow-lg hover:bg-blue-700 transition-colors duration-300 disabled:bg-blue-400 disabled:cursor-not-allowed"
        >
          {isBooking ? 'Booking...' : 'Confirm Booking'}
        </button>
      </form>
    </div>
  );
};

// Use this for local development or for embedding in a browser environment
export default App;
