// Driver Dashboard Component
const DriverDashboard = ({ user, token, showMessage }) => {
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // This is the correct endpoint for a driver's specific assignments.
    // The previous code was trying to fetch ALL bookings, which likely caused the 401 error.
    if (token) {
      const fetchDriverAssignments = async () => {
        try {
          const response = await fetch('http://localhost:5000/api/bookings/mybookings', {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}` // The 'Bearer' prefix is a standard convention for JWTs
        }
      });
          if (!response.ok) {
            throw new Error('Failed to fetch bookings');
          }
          const data = await response.json();
          // The data from the server is now already filtered for this driver.
          setBookings(data);
        } catch (err) {
          console.error('Error fetching driver assignments:', err);
          showMessage('Failed to load your assignments.', 'error');
        } finally {
          setLoading(false);
        }
      };
      fetchDriverAssignments();
    } else {
      // If there's no token, we can just set loading to false and clear bookings.
      setLoading(false);
      setBookings([]);
    }
    // The dependency array now correctly includes 'token' and 'user._id'.
  }, [token, showMessage, user._id]); // Re-run effect when user token or ID changes

  const handleUpdateBookingStatus = (bookingId, status) => {
    // Placeholder for API call to update booking status
    showMessage(`Updating booking ${bookingId} to status: ${status} (functionality to be implemented)`, 'info');
  };

  if (loading) {
    return (
      <div className="d-flex justify-content-center align-items-center" style={{ height: '12rem' }}>
        <p className="text-muted h5">Loading assignments...</p>
        </div>
    );
  }

  return (
    <div className="p-4">
      <h2 className="h3 fw-bold mb-4">Welcome, {user.name}!</h2>
      <p className="h5 text-muted mb-4">Here are your assigned bookings.</p>
      <div className="d-grid gap-3">
        {bookings.length > 0 ? (
          bookings.map(booking => (
            <div key={booking._id} className="card p-3 rounded-3 shadow-sm border">
              <div className="d-flex align-items-center justify-content-between">
                <div>
                  <h3 className="h5 fw-semibold text-primary">Booking #{booking._id.substring(0, 8)}...</h3>
                  <p className="text-muted mb-1">Customer: {booking.customer.user.name}</p>
                  <p className="text-muted mb-1">Status: {booking.status}</p>
                  <p className="text-muted mb-0">Date: {new Date(booking.createdAt).toLocaleDateString()}</p>
                </div>
                <div className="d-flex">
                  <button
                    onClick={() => handleUpdateBookingStatus(booking._id, 'accepted')}
                    className="btn btn-success me-2 rounded-3 shadow-sm"
                  >
                    Accept
                  </button>
                  <button
                    onClick={() => handleUpdateBookingStatus(booking._id, 'declined')}
                    className="btn btn-danger rounded-3 shadow-sm"
                  >
                    Decline
                  </button>
                </div>
              </div>
            </div>
