// Login form component
const Login = ({ onLoginSuccess, showMessage }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);
  // FIX: Removed useNavigate as it's not used in this routing system

  const handleLogin = async (e) => {
    e.preventDefault();
    setError(null); // Clear any previous errors

    try {
      const response = await fetch('http://localhost:5000/api/auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Login failed');
      }

      const { token, user } = await response.json();

      // **Critical step:** Call the onLoginSuccess prop with the user and token
      onLoginSuccess(user, token);
      showMessage('Login successful!', 'success');

    } catch (err) {
      console.error('Login error:', err.message);
      setError(err.message);
      showMessage('Login failed: ' + err.message, 'error');
    }
  };

  return (
    // FIX: Updated Tailwind classes to Bootstrap for consistency
    <div className="d-flex justify-content-center align-items-center h-100 p-4">
      <div className="bg-white p-4 rounded-3 shadow-sm w-100" style={{maxWidth: '24rem'}}>
        <h2 className="h4 fw-bold mb-4 text-center">Login</h2>
        <form onSubmit={handleLogin}>
          {error && <div className="alert alert-danger mb-4 text-center" role="alert">{error}</div>}
          <div className="mb-3">
            <label className="form-label fw-semibold">Email</label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="form-control rounded-3"
              required
            />
          </div>
          <div className="mb-4">
            <label className="form-label fw-semibold">Password</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="form-control rounded-3"
              required
            />
          </div>
          <button
            type="submit"
            className="btn btn-primary w-100 py-2 fw-semibold rounded-3 shadow-sm"
          >
            Login
          </button>
        </form>
      </div>
    </div>
  );
};

// Registration form component
const Register = ({ onRegisterSuccess, showMessage }) => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    phone: '',
    userType: 'customer'
  });
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const response = await fetch('http://localhost:5000/api/auth/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      const data = await response.json();

      if (response.ok) {
        showMessage('Registration successful! Please login.', 'success');
        onRegisterSuccess();
      } else {
        const errorMsg = data.errors?.[0]?.msg || data.msg || 'Registration failed. Please try again.';
        showMessage(errorMsg, 'error');
      }
    } catch (err) {
      showMessage('Network error. Please try again later.', 'error');
      console.error('Registration error:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container p-4 bg-light rounded-3 shadow">
      <h2 className="h3 fw-bold text-center mb-4">Register</h2>
      <form onSubmit={handleSubmit} className="d-grid gap-3">
        <div className="form-group">
          <label className="form-label fw-semibold">Name</label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            className="form-control rounded-3"
            required
          />
        </div>
        <div className="form-group">
          <label className="form-label fw-semibold">Email</label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            className="form-control rounded-3"
            required
          />
        </div>
        <div className="form-group">
          <label className="form-label fw-semibold">Password</label>
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            className="form-control rounded-3"
            required
          />
        </div>
        <div className="form-group">
          <label className="form-label fw-semibold">Phone</label>
          <input
            type="tel"
            name="phone"
            value={formData.phone}
            onChange={handleChange}
            className="form-control rounded-3"
            required
          />
        </div>
        <div className="form-group">
          <label className="form-label fw-semibold">User Type</label>
          <select
            name="userType"
            value={formData.userType}
            onChange={handleChange}
            className="form-select rounded-3"
          >
            <option value="customer">Customer</option>
            <option value="driver">Driver</option>
          </select>
        </div>
        <button
          type="submit"
          disabled={loading}
          className="btn btn-success w-100 py-2 fw-semibold rounded-3 shadow-sm"
        >
          {loading ? 'Registering...' : 'Register'}
        </button>
      </form>
    </div>
  );
};

export default App;
